import React from "react";
import {Card, Input, Select, Button, Table,message} from 'antd';
import '../../api/index'
import {reqUserDataSource} from "../../api";
import columns from "../../config/userColumns";
import data from "../../config/userDataSource";
export default class User extends React.Component{

    state = {
        UserDataSource:[],
    }

    getUserDataSource = () =>{
        const result = reqUserDataSource()
        console.log(result.data)
        if (result.code === 0){
            this.setState({
                UserDataSource:result.data.list
            })
        }
    }

    componentWillMount() {
        this.getUserDataSource()
    }

    render() {
        const { Option } = Select
        const title = (
            <div style={{width:100}}>
                <Input placeholder="用户名" />
                <Select
                    style={{ width: 200 , margin:"0 10px",cursor:"pointer"}}
                    placeholder="选择类型"
                    optionFilterProp="children"
                    filterOption={(input, option) =>
                        option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                    }
                >
                    <Option value="所有">所有</Option>
                    <Option value="管理员">管理员</Option>
                    <Option value="其他用户">其他用户</Option>
                </Select>
                <Button>Search</Button>
            </div>
        )

        return (
            <Card title={title} style={{ width: '100%' }}>
                <Table columns={columns} dataSource={data} />
            </Card>
        );
    }
}
