import menuList from "../../config/menuConfig";
import React from "react";
import { Layout, Menu, Breadcrumb, } from 'antd';
import { Route, Switch } from 'react-router-dom'
import {
    DesktopOutlined,
    PieChartOutlined,
    FileOutlined,
    TeamOutlined,
    UserOutlined,
    AndroidOutlined,
    HomeOutlined
} from '@ant-design/icons';

import UserList from "../user/userList";


const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;



export default class Home extends React.Component{
    state = {
        collapsed: false,
        MenuName:'',
        SubMenuName:'',
        SubMenuKey:'',
    };

    onCollapse = collapsed => {
        console.log(collapsed)
        this.setState({ collapsed });
    };


    getMenuList = (menuList,FItem) => {
        return menuList.map(Item => {
            if (!Item.children){
                return (
                    <Menu.Item key={Item.key} onClick={(item) => {if (item.item.props.openKeys[0] !== this.state.SubMenuKey){if (item.item.props.openKeys[0] === undefined){ console.log('zhixing le ----------'); this.setState({MenuName:Item.title,SubMenuName:''})}else{ console.log('bugaizhixing '); this.setState({ SubMenuName:FItem.title,MenuName:Item.title})} }else {this.setState({MenuName:Item.title})}}}>
                        {Item.title}
                    </Menu.Item>

                // <Menu.Item key={Item.key} onClick={(item) => {if (item.item.props.openKeys[0] !== this.state.SubMenuKey){{this.setState({SubMenuName:FItem.title,MenuName:Item.title})}}else {this.setState({MenuName:Item.title})}}}>
                //     {Item.title}
                // </Menu.Item>

                )
            }else {
                return (
                    <SubMenu key={Item.key} title={Item.title} onTitleClick={(item) => {if (Item.title !== this.state.SubMenuName){this.setState({SubMenuName:Item.title,MenuName:'',SubMenuKey:item.key})}}}>
                        {this.getMenuList(Item.children,Item)}
                    </SubMenu>
                )
            }
        })
    }

    render() {
        return (
            <Layout style={{ minHeight: '100vh' }}>
                <Sider collapsible collapsed={this.state.collapsed} onCollapse={this.onCollapse}>
                    <div className="logo" style={{display:"flex",height:60,alignItems:"center"}}>
                        <img style={{height:32, width:32,margin:"0 10px 0 30px"}} src="https://gw.alipayobjects.com/zos/rmsportal/KDpgvguMpGfqaHPjicRK.svg" alt=""/>
                        <span style={{fontSize:20,color:"white"}}>react-blog</span>
                    </div>
                    <Content>
                        <Switch>
                            <Route path='/user/userLIst' component={UserList}/>
                        </Switch>
                    </Content>
                    <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
                        {this.getMenuList(menuList)}
                    </Menu>
                </Sider>
                <Layout className="site-layout">
                    <Header className="site-layout-background" style={{ padding: 0 }} />
                    <Content style={{ margin: '0 16px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item><span style={{fontSize:20}}>{this.state.SubMenuName}{this.state.MenuName?'/'+this.state.MenuName:null}</span></Breadcrumb.Item>
                        </Breadcrumb>
                        <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                            Bill is a cat.
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>Ant Design ©2018 Created by Ant UED</Footer>
                </Layout>
            </Layout>
        );
    }
}
